import React, { useState } from "react";
import { BookOpen, CheckCircle2, XCircle } from "lucide-react";
import "./SeniorEnglishActivity.css";

const englishQuestions = [
  {
    question: "Identify the verb: 'The cat chased the mouse.'",
    options: ["cat", "chased", "mouse"],
    answer: "chased",
  },
  {
    question: "Choose the correct form: 'She ___ to school every day.'",
    options: ["go", "goes", "going"],
    answer: "goes",
  },
  {
    question: "Find the synonym of 'intelligent':",
    options: ["clever", "lazy", "tired"],
    answer: "clever",
  },
  {
    question: "Select the correct sentence:",
    options: [
      "He don’t like apples.",
      "He doesn’t likes apples.",
      "He doesn’t like apples.",
    ],
    answer: "He doesn’t like apples.",
  },
];

const EnglishActivity = () => {
  const [index, setIndex] = useState(0);
  const [selected, setSelected] = useState("");
  const [feedback, setFeedback] = useState("");
  const [score, setScore] = useState(0);

  const handleSelect = (option) => {
    setSelected(option);
    if (option === englishQuestions[index].answer) {
      setFeedback("correct");
      setScore(score + 1);
      setTimeout(() => nextQuestion(), 1000);
    } else {
      setFeedback("wrong");
    }
  };

  const nextQuestion = () => {
    setSelected("");
    setFeedback("");
    setIndex((index + 1) % englishQuestions.length);
  };

  return (
    <div className="senior-english">
      <h2><BookOpen className="icon" /> English Mastery</h2>

      <p className="question">{englishQuestions[index].question}</p>
      <div className="options">
        {englishQuestions[index].options.map((option) => (
          <button
            key={option}
            className={`option-btn ${selected === option ? "selected" : ""}`}
            onClick={() => handleSelect(option)}
          >
            {option}
          </button>
        ))}
      </div>

      <div className="feedback">
        {feedback === "correct" && <div className="correct"><CheckCircle2 /> Correct!</div>}
        {feedback === "wrong" && <div className="wrong"><XCircle /> Try again!</div>}
      </div>

      <div className="score">Score: {score}</div>
    </div>
  );
};

export default EnglishActivity;
