import React, { useState } from "react";
import { BookOpen, ArrowLeftCircle, Star } from "lucide-react";
import SubjectSelect from "./components/subjectsSelect";
import Activities from "./components/activities";

const App = () => {
  const [selectedSubject, setSelectedSubject] = useState(null);
  const [selectedGrade, setSelectedGrade] = useState(null);

  const gradientBg =
    "linear-gradient(135deg, #3b82f6 0%, #9333ea 50%, #f59e0b 100%)";

  // Step 1: Select Subject
  if (!selectedSubject) {
    return (
      <div
        style={{
          minHeight: "100vh",
          background: gradientBg,
          color: "white",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          textAlign: "center",
          padding: "2rem",
          fontFamily: "'Poppins', sans-serif",
        }}
      >
        <BookOpen size={80} style={{ marginBottom: "1rem" }} />
        <h1 style={{ fontSize: "2.5rem", marginBottom: "1rem" }}>
          Pathfinder Learning Activities
        </h1>
        <p style={{ fontSize: "1.2rem", marginBottom: "2rem" }}>
          Choose a subject to begin your adventure!
        </p>
        <SubjectSelect onSelect={(subject) => setSelectedSubject(subject)} />
      </div>
    );
  }

  // Step 2: Select Grade
  if (!selectedGrade) {
    return (
      <div
        style={{
          minHeight: "100vh",
          background: gradientBg,
          color: "white",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          textAlign: "center",
          padding: "3rem 1rem",
          fontFamily: "'Poppins', sans-serif",
        }}
      >
        <h1 style={{ fontSize: "2.2rem", marginBottom: "1rem" }}>
          {selectedSubject}
        </h1>
        <p style={{ fontSize: "1.1rem", marginBottom: "2rem" }}>
          Select your grade to begin:
        </p>

        <div
          style={{
            display: "flex",
            flexWrap: "wrap",
            justifyContent: "center",
            gap: "1rem",
            maxWidth: "700px",
          }}
        >
          {[1, 2, 3, 4, 5, 6, 7, 8, 9].map((grade) => (
            <button
              key={grade}
              style={{
                background:
                  grade < 4
                    ? "#22c55e"
                    : grade < 7
                    ? "#3b82f6"
                    : "#f97316",
                color: "white",
                border: "none",
                borderRadius: "12px",
                padding: "1rem 1.4rem",
                fontSize: "1.1rem",
                cursor: "pointer",
                boxShadow: "0 6px 15px rgba(0,0,0,0.15)",
                transition: "transform 0.2s ease, box-shadow 0.2s ease",
                display: "flex",
                alignItems: "center",
                gap: "0.5rem",
              }}
              onClick={() => setSelectedGrade(grade)}
              onMouseEnter={(e) =>
                (e.currentTarget.style.transform = "scale(1.1)")
              }
              onMouseLeave={(e) =>
                (e.currentTarget.style.transform = "scale(1)")
              }
            >
              <Star size={20} />
              Grade {grade}
            </button>
          ))}
        </div>

        <div style={{ marginTop: "3rem" }}>
          <button
            onClick={() => setSelectedSubject(null)}
            style={{
              background: "#ef4444",
              color: "white",
              border: "none",
              padding: "0.8rem 1.2rem",
              borderRadius: "10px",
              cursor: "pointer",
              fontSize: "1rem",
              display: "flex",
              alignItems: "center",
              gap: "0.4rem",
              transition: "transform 0.2s ease",
            }}
            onMouseEnter={(e) =>
              (e.currentTarget.style.transform = "scale(1.05)")
            }
            onMouseLeave={(e) =>
              (e.currentTarget.style.transform = "scale(1)")
            }
          >
            <ArrowLeftCircle size={20} />
            Back to Subjects
          </button>
        </div>
      </div>
    );
  }

  // Step 3: Load Activities
  return (
    <Activities
      subject={selectedSubject}
      grade={selectedGrade}
      onBack={() => setSelectedGrade(null)}
    />
  );
};

export default App;
