import React, { useState, useEffect } from "react";
import { Calculator, CheckCircle2, XCircle } from "lucide-react";
import style from"./IntermediateMathActivity.module.css";

const generateQuestion = () => {
  const num1 = Math.floor(Math.random() * 10) + 1;
  const num2 = Math.floor(Math.random() * 10) + 1;
  const operations = ["+", "-"];
  const operation = operations[Math.floor(Math.random() * operations.length)];
  const answer = operation === "+" ? num1 + num2 : num1 - num2;
  return { num1, num2, operation, answer };
};

const MathActivity = () => {
  const [question, setQuestion] = useState(generateQuestion());
  const [input, setInput] = useState("");
  const [feedback, setFeedback] = useState("");
  const [score, setScore] = useState(0);

  const handleSubmit = () => {
    if (parseInt(input) === question.answer) {
      setFeedback("correct");
      setScore(score + 1);
      playSound("success");
      setTimeout(() => {
        setQuestion(generateQuestion());
        setInput("");
        setFeedback("");
      }, 1000);
    } else {
      setFeedback("wrong");
      playSound("error");
    }
  };

  const playSound = (type) => {
  const url =
    type === "success"
      ? "https://actions.google.com/sounds/v1/cartoon/clang_and_wobble.ogg"
      : "https://actions.google.com/sounds/v1/cartoon/wood_plank_flicks.ogg";

  const audio = new Audio(url);
  audio.currentTime = 0;
  audio.volume = 0.7;

  // catch playback errors (some browsers block until user interacts)
  audio.play().catch((err) => {
    console.warn("⚠️ Audio playback blocked or failed:", err);
  });
};


  return (
    <div className="intermediate-math">
      <h2><Calculator className="icon" /> Quick Math!</h2>

      <div className="problem">
        <p className="question">
          {question.num1} {question.operation} {question.num2} = ?
        </p>

        <input
          type="number"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Your answer"
        />
        <button onClick={handleSubmit}>Check</button>
      </div>

      <div className="feedback">
        {feedback === "correct" && (
          <div className="correct"><CheckCircle2 size={20} /> Correct!</div>
        )}
        {feedback === "wrong" && (
          <div className="wrong"><XCircle size={20} /> Try again</div>
        )}
      </div>

      <div className="scoreboard">Score: {score}</div>
    </div>
  );
};

export default MathActivity;
