import React, { useState } from "react";
import { Palette, CheckCircle2, XCircle } from "lucide-react";
import "./SeniorArtActivity.css";

const artQuestions = [
  {
    question: "Which of the following is a principle of design?",
    options: ["Harmony", "Color", "Texture"],
    answer: "Harmony",
  },
  {
    question: "What emotion does the color red often represent?",
    options: ["Calm", "Anger", "Sadness"],
    answer: "Anger",
  },
  {
    question: "Which element of art is about the lightness or darkness of a color?",
    options: ["Value", "Form", "Space"],
    answer: "Value",
  },
  {
    question: "Which artist is known for painting the Mona Lisa?",
    options: ["Leonardo da Vinci", "Pablo Picasso", "Vincent van Gogh"],
    answer: "Leonardo da Vinci",
  },
];

const ArtActivity = () => {
  const [index, setIndex] = useState(0);
  const [selected, setSelected] = useState("");
  const [feedback, setFeedback] = useState("");
  const [score, setScore] = useState(0);

  const handleSelect = (option) => {
    setSelected(option);
    if (option === artQuestions[index].answer) {
      setFeedback("correct");
      setScore(score + 1);
      setTimeout(() => nextQuestion(), 1000);
    } else {
      setFeedback("wrong");
    }
  };

  const nextQuestion = () => {
    setSelected("");
    setFeedback("");
    setIndex((index + 1) % artQuestions.length);
  };

  return (
    <div className="senior-art">
      <h2><Palette className="icon" /> Art & Design Mastery</h2>

      <p className="question">{artQuestions[index].question}</p>
      <div className="options">
        {artQuestions[index].options.map((option) => (
          <button
            key={option}
            className={`option-btn ${selected === option ? "selected" : ""}`}
            onClick={() => handleSelect(option)}
          >
            {option}
          </button>
        ))}
      </div>

      <div className="feedback">
        {feedback === "correct" && <div className="correct"><CheckCircle2 /> Correct!</div>}
        {feedback === "wrong" && <div className="wrong"><XCircle /> Try again!</div>}
      </div>

      <div className="score">Score: {score}</div>
    </div>
  );
};

export default ArtActivity;
