import React, { useState } from "react";
import SubjectSelect from "./components/subjectsSelect";
import Activities from "./components/activities"; 

const App = () => {
  const [selectedSubject, setSelectedSubject] = useState(null);
  const [selectedGrade, setSelectedGrade] = useState(null);

  // Step 1: Select Subject
  if (!selectedSubject) {
    return (
      <div style={{ textAlign: "center", marginTop: "4rem" }}>
        <h1>Pathfinder Learning Activities</h1>
        <SubjectSelect onSelect={(subject) => setSelectedSubject(subject)} />
      </div>
    );
  }

  // Step 2: Select Grade
  if (!selectedGrade) {
    return (
      <div style={{ textAlign: "center", marginTop: "3rem" }}>
        <h1>{selectedSubject}</h1>
        <p>Select your grade to begin:</p>
        {[1, 2, 3, 4, 5, 6, 7, 8, 9].map((grade) => (
          <button
            key={grade}
            style={{
              margin: "0.5rem",
              padding: "0.8rem 1.2rem",
              borderRadius: "8px",
              border: "none",
              background: "#2563eb",
              color: "white",
              cursor: "pointer",
              fontSize: "1rem",
            }}
            onClick={() => setSelectedGrade(grade)}
          >
            Grade {grade}
          </button>
        ))}

        <div style={{ marginTop: "2rem" }}>
          <button
            onClick={() => setSelectedSubject(null)}
            style={{
              background: "#e11d48",
              color: "white",
              border: "none",
              padding: "0.6rem 1rem",
              borderRadius: "6px",
              cursor: "pointer",
            }}
          >
            ‚Üê Back to Subjects
          </button>
        </div>
      </div>
    );
  }

  // Step 3: Load Activities based on grade & subject
  return (
    <Activities
      subject={selectedSubject}
      grade={selectedGrade}
      onBack={() => setSelectedGrade(null)}
    />
  );
};

export default App;
