import React, { useState } from "react";
import { Calculator, CheckCircle2, XCircle } from "lucide-react";
import "./SeniorMathActivity.css";

const mathQuestions = [
  { question: "Solve: 5x = 20", answer: "4" },
  { question: "Simplify: 12 ÷ 3 + 2", answer: "6" },
  { question: "Find x: 3x + 2 = 11", answer: "3" },
  { question: "Simplify: (8 + 4) × 2", answer: "24" },
  { question: "Solve: 15 - (3 × 4)", answer: "3" },
];

const MathActivity = () => {
  const [index, setIndex] = useState(0);
  const [input, setInput] = useState("");
  const [feedback, setFeedback] = useState("");
  const [score, setScore] = useState(0);

  const handleSubmit = () => {
    if (input === mathQuestions[index].answer) {
      setFeedback("correct");
      setScore(score + 1);
      setTimeout(() => nextQuestion(), 1000);
    } else {
      setFeedback("wrong");
    }
  };

  const nextQuestion = () => {
    setInput("");
    setFeedback("");
    setIndex((index + 1) % mathQuestions.length);
  };

  return (
    <div className="senior-math">
      <h2><Calculator className="icon" /> Math Challenge</h2>

      <p className="question">{mathQuestions[index].question}</p>
      <input
        type="text"
        placeholder="Your answer"
        value={input}
        onChange={(e) => setInput(e.target.value)}
      />
      <button onClick={handleSubmit}>Submit</button>

      <div className="feedback">
        {feedback === "correct" && <div className="correct"><CheckCircle2 /> Correct!</div>}
        {feedback === "wrong" && <div className="wrong"><XCircle /> Try again!</div>}
      </div>

      <div className="score">Score: {score}</div>
    </div>
  );
};

export default MathActivity;
