import React, { useState } from "react";
import Confetti from "react-confetti";
import {
  Smile,
  Frown,
  Star,
  Square,
  Circle,
  Triangle,
} from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import "./FoundationActivities.css";

// Import subject-specific activities
import FoundationMathActivity from "./subjects/FoundationMathActivity";
import FoundationEnglishActivity from "./subjects/FoundationEnglishActivity";
import FoundationArtActivity from "./subjects/FoundationArtActivity";

const FoundationActivities = ({ subject, grade, onBack }) => {
  const [showConfetti, setShowConfetti] = useState(false);
  const [feedback, setFeedback] = useState("neutral");
  const [completed, setCompleted] = useState(false);
  const [shapes, setShapes] = useState([
    { id: "circle", label: "Circle", color: "#60a5fa", icon: <Circle /> },
    { id: "square", label: "Square", color: "#f87171", icon: <Square /> },
    { id: "triangle", label: "Triangle", color: "#fbbf24", icon: <Triangle /> },
  ]);
  const [slots] = useState(["circle", "square", "triangle"]);

  // âœ… Play short fun sounds
  const playSound = (type) => {
    const url =
      type === "success"
        ? "https://actions.google.com/sounds/v1/cartoon/clang_and_wobble.ogg"
        : "https://actions.google.com/sounds/v1/cartoon/wood_plank_flicks.ogg";
    const audio = new Audio(url);
    audio.currentTime = 0;
    audio.volume = 0.7;
    audio.play().catch((err) => console.warn("Audio blocked:", err));
  };

  // ðŸ§  Choose subject activity
  const renderActivity = () => {
    switch (subject.toLowerCase()) {
      case "mathematics":
        return <FoundationMathActivity grade={grade} />;
      case "english":
        return <FoundationEnglishActivity grade={grade} />;
      case "art":
        return <FoundationArtActivity grade={grade} />;
      default:
        return <p>No activity found for this subject.</p>;
    }
  };

  // ðŸ”Š Voice feedback
  const speak = (text) => {
    if ("speechSynthesis" in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.lang = "en-US";
      utterance.pitch = 1.1;
      utterance.rate = 0.9;
      utterance.volume = 1;
      window.speechSynthesis.cancel();
      window.speechSynthesis.speak(utterance);
    }
  };

  // ðŸŽ¨ Drag & drop shape matching
 // inside FoundationActivities component â€” replace existing handleDragEnd with this
const handleDragEnd = (result) => {
  console.log("DND result:", result);
  const { source, destination, draggableId } = result;

  // If no destination (dropped outside), do nothing
  if (!destination) {
    setFeedback("neutral");
    return;
  }

  // If dropped back into the same list at the same position, do nothing
  if (
    source.droppableId === destination.droppableId &&
    source.index === destination.index
  ) {
    setFeedback("neutral");
    return;
  }

  // CASE A: re-ordering inside the "shapes" area (optional)
  if (source.droppableId === "shapes" && destination.droppableId === "shapes") {
    const newShapes = Array.from(shapes);
    const [moved] = newShapes.splice(source.index, 1);
    newShapes.splice(destination.index, 0, moved);
    setShapes(newShapes);
    setFeedback("neutral");
    return;
  }

  // CASE B: dragging from shapes into a slot (destination.droppableId is slot id)
  // We want a match only when draggableId === destination.droppableId (e.g., "circle" -> "circle")
  if (source.droppableId === "shapes" && destination.droppableId) {
    const slotId = String(destination.droppableId);
    const draggedId = String(draggableId);

    if (slotId === draggedId) {
      // correct match
      setFeedback("correct");
      playSound("success");
      speak("Great job!");
      setShowConfetti(true);

      // remove the matched shape from the shapes array
      setShapes((prev) => prev.filter((s) => s.id !== draggedId));

      // if this was the last shape, mark completed after a small delay
      if (shapes.length === 1) {
        setTimeout(() => setCompleted(true), 400);
        speak("You matched all the shapes! Well done!");
      }

      // hide confetti after a while
      setTimeout(() => setShowConfetti(false), 2000);
      return;
    } else {
      // wrong slot
      setFeedback("wrong");
      playSound("error");
      speak("Oops! Try again!");
      return;
    }
  }

  // Any other drop scenario: keep neutral (or add handling if needed)
  setFeedback("neutral");
};

};

// ðŸ§¸ Mascot animations
const AnimatedMascot = ({ feedback }) => {
  const variants = {
    neutral: { scale: 1 },
    correct: {
      scale: [1, 1.2, 1],
      rotate: [0, 10, -10, 0],
      transition: { duration: 0.8 },
    },
    wrong: {
      scale: [1, 0.9, 1],
      rotate: [0, -10, 10, 0],
      transition: { duration: 0.6 },
    },
  };

  return (
    <motion.div className="mascot" variants={variants} animate={feedback}>
      <AnimatePresence mode="wait">
        {feedback === "correct" && (
          <Smile size={70} color="#10b981" key="happy" />
        )}
        {feedback === "wrong" && <Frown size={70} color="#ef4444" key="sad" />}
        {feedback === "neutral" && (
          <Smile size={70} color="#3b82f6" key="neutral" />
        )}
      </AnimatePresence>
    </motion.div>
  );
};

export default FoundationActivities;
