import React, { useState, useMemo, useCallback, useEffect } from "react";
import PropTypes from "prop-types";
import Confetti from "react-confetti";
import { Smile, Frown, Star, Square, Circle, Triangle } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import styles from "./FoundationActivities.module.css";

// ✅ Import subject-specific activities
import FoundationMathActivity from "./subjects/FoundationMathActivity";
import FoundationEnglishActivity from "./subjects/FoundationEnglishActivity";
import FoundationArtActivity from "./subjects/FoundationArtActivity";
import ErrorBoundary from "./ErrorBoundary";
import LoadingSpinner from "./LoadingSpinner";

// 🎯 Constants and Configuration
const GAME_CONFIG = {
  sounds: {
    success: "https://actions.google.com/sounds/v1/cartoon/clang_and_wobble.ogg",
    error: "https://actions.google.com/sounds/v1/cartoon/wood_plank_flicks.ogg"
  },
  speech: {
    pitch: 1.1,
    rate: 0.9,
    lang: 'en-US'
  },
  feedback: {
    correct: "Great job!",
    wrong: "Oops! Try again!",
    complete: "You matched all the shapes! Well done!"
  }
};

const SUBJECTS = {
  MATHEMATICS: 'mathematics',
  ENGLISH: 'english',
  ART: 'art'
};

const INITIAL_SHAPES = [
  { id: "circle", label: "Circle", color: "#60a5fa", icon: <Circle /> },
  { id: "square", label: "Square", color: "#f87171", icon: <Square /> },
  { id: "triangle", label: "Triangle", color: "#fbbf24", icon: <Triangle /> },
];

// 🎵 Custom Hook for Audio Management
const useAudio = () => {
  const [audioElements] = useState(() => ({
    success: new Audio(GAME_CONFIG.sounds.success),
    error: new Audio(GAME_CONFIG.sounds.error)
  }));

  useEffect(() => {
    // Preload audio
    Object.values(audioElements).forEach(audio => {
      audio.load();
    });

    return () => {
      // Cleanup
      Object.values(audioElements).forEach(audio => {
        audio.pause();
        audio.src = '';
      });
    };
  }, [audioElements]);

  const playSound = useCallback((type) => {
    const audio = audioElements[type];
    if (audio) {
      audio.currentTime = 0;
      audio.volume = 0.7;
      audio.play().catch(() => {
        // Silent fail for autoplay restrictions
        console.warn('Audio playback blocked');
      });
    }
  }, [audioElements]);

  return { playSound };
};

// 🗣️ Custom Hook for Speech Synthesis
const useSpeech = () => {
  const speak = useCallback((text) => {
    if ("speechSynthesis" in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.lang = GAME_CONFIG.speech.lang;
      utterance.pitch = GAME_CONFIG.speech.pitch;
      utterance.rate = GAME_CONFIG.speech.rate;
      utterance.volume = 1;
      
      window.speechSynthesis.cancel();
      window.speechSynthesis.speak(utterance);
    }
  }, []);

  return { speak };
};

// 🎮 Custom Hook for Shape Matching Game Logic
const useShapeMatchingGame = (grade) => {
  const [shapes, setShapes] = useState(INITIAL_SHAPES);
  const [feedback, setFeedback] = useState("neutral");
  const [completed, setCompleted] = useState(false);
  const [attempts, setAttempts] = useState(0);

  const slots = useMemo(() => ["circle", "square", "triangle"], []);

  const { playSound } = useAudio();
  const { speak } = useSpeech();

  const handleDragEnd = useCallback((result) => {
    if (!result.destination) return;

    setAttempts(prev => prev + 1);

    if (result.destination.droppableId === result.draggableId) {
      setFeedback("correct");
      playSound("success");
      speak(GAME_CONFIG.feedback.correct);
      
      setShapes((prev) => prev.filter((s) => s.id !== result.draggableId));

      if (shapes.length === 1) {
        setTimeout(() => {
          setCompleted(true);
          speak(GAME_CONFIG.feedback.complete);
        }, 400);
      }
    } else {
      setFeedback("wrong");
      playSound("error");
      speak(GAME_CONFIG.feedback.wrong);
    }

    // Reset feedback after delay
    setTimeout(() => setFeedback("neutral"), 2000);
  }, [shapes.length, playSound, speak]);

  const resetGame = useCallback(() => {
    setShapes(INITIAL_SHAPES);
    setCompleted(false);
    setFeedback("neutral");
    setAttempts(0);
  }, []);

  return {
    shapes,
    slots,
    feedback,
    completed,
    attempts,
    handleDragEnd,
    resetGame
  };
};

// 🧩 Memoized Draggable Shape Component
const DraggableShape = React.memo(({ shape, index }) => (
  <Draggable draggableId={shape.id} index={index}>
    {(provided, snapshot) => (
      <motion.div
        className={styles.shapeItem}
        ref={provided.innerRef}
        {...provided.draggableProps}
        {...provided.dragHandleProps}
        style={{
          backgroundColor: shape.color,
          opacity: snapshot.isDragging ? 0.8 : 1,
          ...provided.draggableProps.style,
        }}
        whileTap={{ scale: 0.9 }}
        aria-label={`Drag ${shape.label} shape`}
      >
        {shape.icon}
        <span className="sr-only">{shape.label}</span>
      </motion.div>
    )}
  </Draggable>
));

// 🎯 Shape Matching Game Component
const ShapeMatchingGame = ({ feedback, completed, shapes, slots, handleDragEnd }) => {
  return (
    <div className={styles.shapeSection}>
      <h3 className={styles.subTitle}>Shape Matching Game</h3>
      <p className={styles.instructions}>Drag the shapes into the correct outlines!</p>

      <DragDropContext onDragEnd={handleDragEnd}>
        <div className={styles.shapeBoard}>
          <Droppable droppableId="shapes" direction="horizontal">
            {(provided) => (
              <div
                className={styles.shapeRow}
                ref={provided.innerRef}
                {...provided.droppableProps}
                aria-label="Available shapes"
              >
                {shapes.map((shape, index) => (
                  <DraggableShape 
                    key={shape.id} 
                    shape={shape} 
                    index={index} 
                  />
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>

          <div className={styles.slotsRow}>
            {slots.map((slot) => (
              <Droppable droppableId={slot} key={slot}>
                {(provided) => (
                  <div
                    className={styles.shapeSlot}
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                    aria-label={`Drop zone for ${slot} shape`}
                  >
                    {slot === "circle" && <Circle color="#60a5fa" size={70} />}
                    {slot === "square" && <Square color="#f87171" size={70} />}
                    {slot === "triangle" && <Triangle color="#fbbf24" size={70} />}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            ))}
          </div>
        </div>
      </DragDropContext>

      {completed && (
        <motion.div
          className={styles.congrats}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          role="alert"
          aria-live="polite"
        >
          <Star size={70} color="#facc15" />
          <p>Excellent work! 🎉</p>
        </motion.div>
      )}
    </div>
  );
};

// 📚 Activity Renderer with Error Boundary
const ActivityRenderer = React.memo(({ subject, grade }) => {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Simulate loading time for activities
    const timer = setTimeout(() => setIsLoading(false), 800);
    return () => clearTimeout(timer);
  }, [subject]);

  if (isLoading) {
    return <LoadingSpinner />;
  }

  return (
    <ErrorBoundary fallback={<ErrorActivity subject={subject} />}>
      {(() => {
        switch (subject.toLowerCase()) {
          case SUBJECTS.MATHEMATICS:
            return <FoundationMathActivity grade={grade} />;
          case SUBJECTS.ENGLISH:
            return <FoundationEnglishActivity grade={grade} />;
          case SUBJECTS.ART:
            return <FoundationArtActivity grade={grade} />;
          default:
            return <FallbackActivity subject={subject} />;
        }
      })()}
    </ErrorBoundary>
  );
});

// 🆘 Fallback Components
const ErrorActivity = ({ subject, error }) => (
  <div className={styles.errorActivity} role="alert">
    <Frown size={48} color="#ef4444" />
    <h4>Something went wrong with {subject}</h4>
    <p>We're having trouble loading this activity. Please try again.</p>
    {error && <details><summary>Technical details</summary>{error.message}</details>}
  </div>
);

const FallbackActivity = ({ subject }) => (
  <div className={styles.fallbackActivity}>
    <p>No activity found for {subject}.</p>
    <p>New activities are coming soon! 🚀</p>
  </div>
);

// 🧸 Mascot Component (Optimized)
const AnimatedMascot = React.memo(({ feedback }) => {
  const variants = {
    neutral: { scale: 1 },
    correct: {
      scale: [1, 1.2, 1],
      rotate: [0, 10, -10, 0],
      transition: { duration: 0.8 },
    },
    wrong: {
      scale: [1, 0.9, 1],
      rotate: [0, -10, 10, 0],
      transition: { duration: 0.6 },
    },
  };

  return (
    <motion.div 
      className={styles.mascot} 
      variants={variants} 
      animate={feedback}
      aria-live="polite"
      aria-label={`Mascot is ${feedback}`}
    >
      <AnimatePresence mode="wait">
        {feedback === "correct" && (
          <Smile size={80} color="#10b981" key="happy" />
        )}
        {feedback === "wrong" && <Frown size={80} color="#ef4444" key="sad" />}
        {feedback === "neutral" && (
          <Smile size={80} color="#3b82f6" key="neutral" />
        )}
      </AnimatePresence>
    </motion.div>
  );
});

// 🎯 Main Component
const FoundationActivities = ({ subject, grade, onBack, studentId, onActivityComplete }) => {
  const [showConfetti, setShowConfetti] = useState(false);
  const [accessibilityFeedback, setAccessibilityFeedback] = useState("");

  const {
    shapes,
    slots,
    feedback,
    completed,
    attempts,
    handleDragEnd,
    resetGame
  } = useShapeMatchingGame(grade);

  // 🎉 Confetti effect
  useEffect(() => {
    if (completed) {
      setShowConfetti(true);
      const timer = setTimeout(() => setShowConfetti(false), 3000);
      return () => clearTimeout(timer);
    }
  }, [completed]);

  // 📊 Track learning analytics
  useEffect(() => {
    if (completed && studentId) {
      trackStudentProgress({
        activity: 'shape-matching',
        subject,
        grade,
        studentId,
        outcome: 'completed',
        attempts,
        timestamp: new Date().toISOString()
      });
      
      if (onActivityComplete) {
        onActivityComplete({
          subject,
          grade,
          activity: 'shape-matching',
          score: 1,
          timeSpent: 0 // You would calculate this
        });
      }
    }
  }, [completed, attempts, subject, grade, studentId, onActivityComplete]);

  const provideAccessibleFeedback = useCallback((message) => {
    setAccessibilityFeedback(message);
    // This will be announced by screen readers
  }, []);

  return (
    <div className={styles.foundationContainer}>
      {/* 🎊 Confetti */}
      {showConfetti && <Confetti recycle={false} numberOfPieces={200} />}
      
      {/* 🗣️ Accessibility Feedback (hidden visually, announced by screen readers) */}
      <div 
        id="live-feedback" 
        className="sr-only" 
        aria-live="polite" 
        aria-atomic="true"
      >
        {accessibilityFeedback}
      </div>

      {/* 🔙 Navigation */}
      <button 
        className={styles.backButton} 
        onClick={onBack}
        aria-label="Go back to subject selection"
      >
        ← Back
      </button>

      {/* 📝 Header */}
      <h1 className={styles.activityTitle}>{subject}</h1>
      <p className={styles.gradeInfo}>Grade {grade} | Foundation Phase</p>

      {/* 🧸 Mascot */}
      <AnimatedMascot feedback={feedback} />

      {/* 🎮 Activities Grid */}
      <div className={styles.activitiesWrapper}>
        <ShapeMatchingGame
          feedback={feedback}
          completed={completed}
          shapes={shapes}
          slots={slots}
          handleDragEnd={handleDragEnd}
        />

        <div className={styles.subjectSection}>
          <ActivityRenderer subject={subject} grade={grade} />
        </div>
      </div>

      {/* 🔄 Reset Game Button */}
      {completed && (
        <motion.div
          className={styles.completionActions}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
        >
          <button 
            className={styles.resetButton}
            onClick={resetGame}
            aria-label="Play shape matching game again"
          >
            <Star size={20} />
            Play Again
          </button>
        </motion.div>
      )}
    </div>
  );
};

// 📊 Analytics Function
const trackStudentProgress = (event) => {
  // In a real app, this would send to your backend
  console.log('Learning Analytics:', event);
  
  // Example implementation:
  // fetch('/api/learning-events', {
  //   method: 'POST',
  //   headers: { 'Content-Type': 'application/json' },
  //   body: JSON.stringify(event)
  // });
};

// 🔧 PropTypes for Type Safety
FoundationActivities.propTypes = {
  subject: PropTypes.oneOf(['mathematics', 'english', 'art']).isRequired,
  grade: PropTypes.number.isRequired,
  onBack: PropTypes.func.isRequired,
  studentId: PropTypes.string,
  onActivityComplete: PropTypes.func
};

export default FoundationActivities;
