import React, { useState } from "react";
import { Calculator, CheckCircle2, XCircle } from "lucide-react";
import "./FoundationMathActivity.css";

// Function to generate random math questions
const generateQuestion = () => {
  const num1 = Math.floor(Math.random() * 10) + 1;
  const num2 = Math.floor(Math.random() * 10) + 1;
  const operations = ["+", "-"];
  const operation = operations[Math.floor(Math.random() * operations.length)];
  const answer = operation === "+" ? num1 + num2 : num1 - num2;
  return { num1, num2, operation, answer };
};

const FoundationMathActivity = () => {
  const [question, setQuestion] = useState(generateQuestion());
  const [input, setInput] = useState("");
  const [feedback, setFeedback] = useState("");
  const [score, setScore] = useState(0);

  const handleSubmit = () => {
    if (parseInt(input) === question.answer) {
      setFeedback("correct");
      setScore(score + 1);
      playSound("success");
      setTimeout(() => {
        setQuestion(generateQuestion());
        setInput("");
        setFeedback("");
      }, 1000);
    } else {
      setFeedback("wrong");
      playSound("error");
    }
  };

  // ✅ Uses stable Mixkit URLs for sound
  const playSound = (type) => {
    const audio = new Audio(
      type === "success"
        ? "https://assets.mixkit.co/sfx/preview/mixkit-achievement-bell-600.mp3"
        : "https://assets.mixkit.co/sfx/preview/mixkit-funny-fail-low-tone-2876.mp3"
    );

    audio
      .play()
      .then(() => console.log(`${type} sound played`))
      .catch((err) => console.warn("Playback issue:", err));
  };

  return (
    <div className="foundation-math">
      <h2>
        <Calculator className="icon" /> Quick Math!
      </h2>

      <div className="problem">
        <p className="question">
          {question.num1} {question.operation} {question.num2} = ?
        </p>

        <input
          type="number"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Your answer"
        />
        <button onClick={handleSubmit}>Check</button>
      </div>

      <div className="feedback">
        {feedback === "correct" && (
          <div className="correct">
            <CheckCircle2 size={20} /> Correct!
          </div>
        )}
        {feedback === "wrong" && (
          <div className="wrong">
            <XCircle size={20} /> Try again
          </div>
        )}
      </div>

      <div className="scoreboard">Score: {score}</div>
    </div>
  );
};

export default FoundationMathActivity;
