import React, { useState } from "react";
import { Paintbrush, CheckCircle2, XCircle } from "lucide-react";
import "./IntermediateArtActivity.css";

const questions = [
  { 
    question: "What colors make green?", 
    options: ["Red + Blue", "Blue + Yellow", "Red + Yellow"], 
    answer: "Blue + Yellow" 
  },
  { 
    question: "Which of these is a warm color?", 
    options: ["Blue", "Red", "Green"], 
    answer: "Red" 
  },
  { 
    question: "Which color is opposite of blue on the color wheel?", 
    options: ["Yellow", "Orange", "Orange-red"], 
    answer: "Orange" 
  },
  { 
    question: "What do we call a light version of a color?", 
    options: ["Tint", "Shade", "Tone"], 
    answer: "Tint" 
  },
];

const ArtActivity = () => {
  const [index, setIndex] = useState(0);
  const [selected, setSelected] = useState("");
  const [feedback, setFeedback] = useState("");
  const [score, setScore] = useState(0);

  const handleSelect = (option) => {
    setSelected(option);
    if (option === questions[index].answer) {
      setFeedback("correct");
      setScore(score + 1);
      playSound("success");
      setTimeout(() => nextQuestion(), 1000);
    } else {
      setFeedback("wrong");
      playSound("error");
    }
  };

  const nextQuestion = () => {
    setFeedback("");
    setSelected("");
    if (index < questions.length - 1) setIndex(index + 1);
    else setIndex(0);
  };

  const playSound = (type) => {
  const url =
    type === "success"
      ? "https://actions.google.com/sounds/v1/cartoon/clang_and_wobble.ogg"
      : "https://actions.google.com/sounds/v1/cartoon/wood_plank_flicks.ogg";

  const audio = new Audio(url);
  audio.currentTime = 0;
  audio.volume = 0.7;

  // catch playback errors (some browsers block until user interacts)
  audio.play().catch((err) => {
    console.warn("⚠️ Audio playback blocked or failed:", err);
  });
};



  return (
    <div className="art-activity">
      <h2><Paintbrush className="icon" /> Art & Color Quiz</h2>

      <p className="question">{questions[index].question}</p>
      <div className="options">
        {questions[index].options.map((option) => (
          <button
            key={option}
            className={`option-btn ${selected === option ? "selected" : ""}`}
            onClick={() => handleSelect(option)}
          >
            {option}
          </button>
        ))}
      </div>

      <div className="feedback">
        {feedback === "correct" && <div className="correct"><CheckCircle2 /> Correct!</div>}
        {feedback === "wrong" && <div className="wrong"><XCircle /> Try again!</div>}
      </div>

      <div className="scoreboard">Score: {score}</div>
    </div>
  );
};

export default ArtActivity;
