import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { BookOpen, Award, CheckCircle, XCircle } from "lucide-react";
import styles from "./SeniorActivities.module.css";

import SeniorMathActivity from "./subjects/SeniorMathActivity";
import SeniorEnglishActivity from "./subjects/SeniorEnglishActivity";
import SeniorArtActivity from "./subjects/SeniorArtActivity";

import { supabase } from "../../../lib/supabaseClient";

const SeniorActivities = ({ subject, grade, onBack }) => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [showResult, setShowResult] = useState(false);

  // ✅ Tracking metrics
  const [userStats, setUserStats] = useState([]);
  const [questionStartTime, setQuestionStartTime] = useState(Date.now());
  const [retries, setRetries] = useState(0);

  // ✅ Supabase testing states
  const [testResult, setTestResult] = useState("");
  const [fetchedData, setFetchedData] = useState([]);

  const questions = [
    { question: "Which of the following is a prime number?", options: ["12", "17", "21", "27"], correct: "17" },
    { question: "Simplify: 3(x + 4) = ?", options: ["3x + 4", "x + 12", "3x + 12", "7x"], correct: "3x + 12" },
    { question: "If y = 2x + 3 and x = 5, find y.", options: ["10", "11", "13", "7"], correct: "13" },
  ];

  const handleAnswer = (option, usedVisual = true, usedDrawing = false) => {
    const timeSpent = Date.now() - questionStartTime;
    const correct = option === questions[currentQuestion].correct;

    // Store stats for this question
    setUserStats((prev) => [
      ...prev,
      {
        subject,
        grade,
        timeSpent,
        retries,
        usedVisual,
        usedDrawing,
        correct,
      },
    ]);

    if (!correct) setRetries(retries + 1);
    setSelectedAnswer(option);

    setTimeout(() => {
      if (currentQuestion + 1 < questions.length) {
        setCurrentQuestion(currentQuestion + 1);
        setSelectedAnswer(null);
        setQuestionStartTime(Date.now());
        setRetries(0);
      } else {
        setShowResult(true);
      }
    }, 800);
  };

  const restartQuiz = () => {
    setCurrentQuestion(0);
    setSelectedAnswer(null);
    setShowResult(false);
    setUserStats([]);
    setRetries(0);
    setQuestionStartTime(Date.now());
  };

  const renderActivity = () => {
    switch (subject.toLowerCase()) {
      case "mathematics":
        return <SeniorMathActivity grade={grade} />;
      case "english":
        return <SeniorEnglishActivity grade={grade} />;
      case "art":
        return <SeniorArtActivity grade={grade} />;
      default:
        return <p>No activity found for this subject.</p>;
    }
  };

  // ✅ Send metrics to Supabase after quiz completion
  useEffect(() => {
    if (!showResult || userStats.length === 0) return;

    const avgTime = userStats.reduce((acc, q) => acc + q.timeSpent, 0) / userStats.length;
    const avgAccuracy = (userStats.filter((q) => q.correct).length / userStats.length) * 100;
    const avgRetries = userStats.reduce((acc, q) => acc + q.retries, 0) / userStats.length;
    const percentDrawing = (userStats.filter((q) => q.usedDrawing).length / userStats.length) * 100;
    const percentVisual = (userStats.filter((q) => q.usedVisual).length / userStats.length) * 100;

    const stats = {
      student_id: "mock-student-001", // replace with actual student ID
      subject,
      grade,
      avg_time: avgTime,
      avg_accuracy: avgAccuracy,
      avg_retries: avgRetries,
      percent_drawing: percentDrawing,
      percent_visual: percentVisual,
      created_at: new Date().toISOString(),
    };

    supabase
      .from("student_activity_stats")
      .insert([stats])
      .then(({ error }) => {
        if (error) console.error("Failed to save stats:", error);
        else console.log("Stats saved successfully!");
      });
  }, [showResult]);

  // ✅ Supabase Test Insert
  const insertTestRow = async () => {
    setTestResult("⏳ Inserting test row...");
    try {
      const testData = {
        student_id: "TEST_SNR001",
        subject,
        grade,
        avg_time: 22,
        avg_accuracy: 91,
        avg_retries: 1,
        percent_drawing: 30,
        percent_visual: 70,
        percent_numeric: 50,
        uses_drawing_ratio: 0.3,
        created_at: new Date().toISOString(),
      };

      const { data, error } = await supabase
        .from("student_activity_stats")
        .insert([testData])
        .select();

      if (error) {
        console.error("❌ Test insert failed:", error);
        setTestResult("❌ Insert failed: " + error.message);
      } else {
        console.log("✅ Test insert successful:", data);
        setTestResult("✅ Test insert successful! Check Supabase table.");
      }
    } catch (err) {
      console.error("Unexpected error:", err);
      setTestResult("⚠️ Unexpected error: " + err.message);
    }
  };

  // ✅ Supabase Test Fetch
  const fetchAllRows = async () => {
    setTestResult("⏳ Fetching rows...");
    try {
      const { data, error } = await supabase
        .from("student_activity_stats")
        .select("*")
        .order("created_at", { ascending: false })
        .limit(5);

      if (error) {
        console.error("❌ Fetch failed:", error);
        setTestResult("❌ Fetch failed: " + error.message);
      } else {
        console.log("✅ Fetched rows:", data);
        setFetchedData(data);
        setTestResult(`✅ Fetched ${data.length} recent rows`);
      }
    } catch (err) {
      console.error("Unexpected error:", err);
      setTestResult("⚠️ Unexpected error: " + err.message);
    }
  };

  return (
    <div className={styles.container}>
      <button className={styles.backButton} onClick={onBack}>← Back</button>

      {/* 🔹 Supabase Test Buttons */}
      <div style={{ margin: "10px 0" }}>
        <button
          style={{
            marginRight: "10px",
            padding: "6px 12px",
            background: "#2563eb",
            color: "white",
            borderRadius: "5px",
          }}
          onClick={insertTestRow}
        >
          Test Supabase Insert
        </button>
        <button
          style={{
            padding: "6px 12px",
            background: "#10b981",
            color: "white",
            borderRadius: "5px",
          }}
          onClick={fetchAllRows}
        >
          Fetch All Rows
        </button>
      </div>

      {/* ✅ Display test results */}
      {testResult && <p style={{ marginTop: "8px", fontWeight: 500 }}>{testResult}</p>}

      {fetchedData.length > 0 && (
        <div
          style={{
            marginTop: "10px",
            background: "#f3f4f6",
            padding: "10px",
            borderRadius: "6px",
          }}
        >
          <h4>Recent Entries:</h4>
          <ul>
            {fetchedData.map((row) => (
              <li key={row.id}>
                {row.student_id} — {row.subject} ({row.grade}) | Accuracy: {row.avg_accuracy}%
              </li>
            ))}
          </ul>
        </div>
      )}

      <div className={styles.header}>
        <BookOpen size={48} color="#1e3a8a" />
        <h2>{subject} - Senior Activity</h2>
      </div>
      <p className={styles.gradeInfo}>Grade {grade} | Senior Phase</p>

      <div className={styles.mainContent}>
        <div className={styles.subjectPanel}>{renderActivity()}</div>

        <div className={styles.quizPanel}>
          {!showResult ? (
            <motion.div
              className={styles.quizCard}
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
            >
              <h3 className={styles.questionText}>
                {currentQuestion + 1}. {questions[currentQuestion].question}
              </h3>

              <div className={styles.options}>
                {questions[currentQuestion].options.map((option, i) => (
                  <motion.button
                    key={i}
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    className={`${styles.optionBtn} ${
                      selectedAnswer === option
                        ? option === questions[currentQuestion].correct
                          ? styles.correct
                          : styles.wrong
                        : ""
                    }`}
                    onClick={() => handleAnswer(option)}
                    disabled={selectedAnswer !== null}
                  >
                    {option}
                  </motion.button>
                ))}
              </div>

              <div className={styles.progress}>
                Question {currentQuestion + 1} of {questions.length}
              </div>
            </motion.div>
          ) : (
            <motion.div
              className={styles.resultCard}
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
            >
              <Award size={60} color="#f59e0b" />
              <h3>Quiz Complete!</h3>
              <p>
                You scored <strong>{userStats.filter((q) => q.correct).length}</strong> out of{" "}
                {questions.length}
              </p>

              {userStats.filter((q) => q.correct).length / questions.length >= 0.7 ? (
                <div className={`${styles.feedback} ${styles.correct}`}>
                  <CheckCircle size={32} color="#16a34a" />
                  <p>Excellent work — you're mastering this topic!</p>
                </div>
              ) : (
                <div className={`${styles.feedback} ${styles.wrong}`}>
                  <XCircle size={32} color="#ef4444" />
                  <p>Keep practicing — you’ll get there!</p>
                </div>
              )}

              <button className={styles.retryButton} onClick={restartQuiz}>
                Retry Quiz
              </button>
            </motion.div>
          )}
        </div>
      </div>
    </div>
  );
};

export default SeniorActivities;
