import React, { useState } from "react";
import { Palette, CheckCircle, XCircle } from "lucide-react";
import "./FoundationArtActivity.css";

const colors = ["red", "blue", "yellow", "green", "purple"];

const ArtsActivity = () => {
  const [targetColor, setTargetColor] = useState(randomColor());
  const [feedback, setFeedback] = useState("");
  const [score, setScore] = useState(0);

  function randomColor() {
    const colorOptions = ["red", "blue", "yellow", "green", "purple"];
    return colorOptions[Math.floor(Math.random() * colorOptions.length)];
  }

  const handleColorClick = (color) => {
    if (color === targetColor) {
      setFeedback("correct");
      setScore(score + 1);
      playSound("success");
      setTimeout(() => {
        setTargetColor(randomColor());
        setFeedback("");
      }, 1000);
    } else {
      setFeedback("wrong");
      playSound("error");
    }
  };

const playSound = (type) => {
  const audio = new Audio(
    type === "success"
      ? "https://assets.mixkit.co/sfx/preview/mixkit-achievement-bell-600.mp3"
      : "https://assets.mixkit.co/sfx/preview/mixkit-wrong-answer-fail-notification-946.mp3"
  );

  // Prevents browser autoplay restrictions from breaking it
  audio
    .play()
    .then(() => console.log(`${type} sound played`))
    .catch((err) => console.log("Audio playback blocked:", err));
};


  return (
    <div className="arts-activity">
      <h2><Palette className="icon" /> Color Match!</h2>
      <p className="instruction">Tap the color that matches <strong>{targetColor.toUpperCase()}</strong></p>

      <div className="color-grid">
        {colors.map((color) => (
          <button
            key={color}
            className="color-btn"
            style={{ backgroundColor: color }}
            onClick={() => handleColorClick(color)}
          />
        ))}
      </div>

      <div className="feedback">
        {feedback === "correct" && (
          <div className="correct"><CheckCircle size={20} /> Well done!</div>
        )}
        {feedback === "wrong" && (
          <div className="wrong"><XCircle size={20} /> Try again!</div>
        )}
      </div>

      <div className="scoreboard">Score: {score}</div>
    </div>
  );
};

export default ArtsActivity;
