import React, { useState, useEffect } from "react";
import { Volume2, Smile, BookOpen } from "lucide-react";
import "./FoundationEnglishActivity.css";

const letters = [
  { letter: "A", word: "Apple", sound: "https://cdn.pixabay.com/download/audio/2021/08/04/audio_5a2e3c6760.mp3?filename=click.wav" },
  { letter: "B", word: "Ball", sound: "https://cdn.pixabay.com/download/audio/2022/03/15/audio_1a3c7b9dc3.mp3?filename=success-1-6297.mp3" },
  { letter: "C", word: "Cat", sound: "https://cdn.pixabay.com/download/audio/2022/03/15/audio_00f9c8d21f.mp3?filename=error-1-126517.mp3" },
];

const EnglishActivity = () => {
  const [current, setCurrent] = useState(0);
  const [feedback, setFeedback] = useState("");
  const [input, setInput] = useState("");
  const [score, setScore] = useState(0);

  const currentLetter = letters[current];

  const playSound = () => {
    const audio = new Audio(currentLetter.sound);
    audio.play();
  };

  const handleSubmit = () => {
    if (input.toUpperCase() === currentLetter.letter) {
      setFeedback("correct");
      setScore(score + 1);
      setTimeout(() => {
        nextLetter();
      }, 1000);
    } else {
      setFeedback("wrong");
    }
  };

  const nextLetter = () => {
    const next = (current + 1) % letters.length;
    setCurrent(next);
    setFeedback("");
    setInput("");
  };

  return (
    <div className="english-activity">
      <h2><BookOpen className="icon" /> Learn Letters!</h2>

      <div className="letter-card">
        <div className="letter">{currentLetter.letter}</div>
        <p className="word">for {currentLetter.word}</p>
        <button className="sound-btn" onClick={playSound}>
          <Volume2 size={20} /> Hear Sound
        </button>
      </div>

      <div className="question">
        <p>Type the letter you see:</p>
        <input
          type="text"
          maxLength={1}
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <button onClick={handleSubmit}>Check</button>
      </div>

      <div className="feedback">
        {feedback === "correct" && <div className="correct">✅ Great Job!</div>}
        {feedback === "wrong" && <div className="wrong">❌ Try Again!</div>}
      </div>

      <div className="scoreboard">
        <Smile size={20} /> Score: {score}
      </div>
    </div>
  );
};

export default EnglishActivity;
