import React, { useState } from "react";
import { motion } from "framer-motion";
import Confetti from "react-confetti";
import { Brain, Star, CheckCircle, XCircle } from "lucide-react";
import "./IntermediateActivities.css";
import IntermediateMathActivity from "./subjects/IntermediateMathActivity";
import IntermediateEnglishActivity from "./subjects/IntermediateEnglishActivity";
import IntermediateArtActivity from "./subjects/IntermediateArtActivity";

const IntermediateActivities = ({ subject, grade, onBack }) => {
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [feedback, setFeedback] = useState(null);
  const [completed, setCompleted] = useState(false);

  // üéµ Sound feedback helper
  const playSound = (type) => {
    const audio = new Audio(
      type === "success"
        ? "https://cdn.pixabay.com/download/audio/2022/03/15/audio_1a3c7b9dc3.mp3?filename=success-1-6297.mp3"
        : "https://cdn.pixabay.com/download/audio/2022/03/15/audio_00f9c8d21f.mp3?filename=error-1-126517.mp3"
    );
    audio.play();
  };

  // üßÆ Universal warm-up question
  const patternQuestion = {
    question: "Which shape completes the pattern?",
    sequence: ["üî∫", "üîπ", "üî∫", "üîπ", "‚ùì"],
    options: ["üî∫", "üî∏", "üîπ", "‚ö´"],
    correct: "üî∫",
  };

  const handleAnswer = (option) => {
    setSelectedAnswer(option);
    if (option === patternQuestion.correct) {
      setFeedback("correct");
      playSound("success");
      setCompleted(true);
    } else {
      setFeedback("wrong");
      playSound("error");
    }
  };

  // üé® Render the correct subject activity dynamically
  const renderActivity = () => {
    switch (subject.toLowerCase()) {
      case "mathematics":
        return <IntermediateMathActivity grade={grade} />;
      case "english":
        return <IntermediateEnglishActivity grade={grade} />;
      case "art":
        return <IntermediateArtActivity grade={grade} />;
      default:
        return <p>No activity found for this subject.</p>;
    }
  };

  return (
    <div className="intermediate-activities">
      {completed && <Confetti numberOfPieces={150} recycle={false} />}

      {/* üîô Back Button */}
      <button className="back-btn" onClick={onBack}>
        ‚Üê Back
      </button>

      {/* üß† Header */}
      <div className="header">
        <Brain size={40} color="#2563eb" />
        <h2>{subject} - Intermediate Activity</h2>
      </div>
      <p className="grade-info">Grade {grade} | Intermediate Phase</p>

      {/* üß© Pattern Warm-Up */}
      <motion.div
        className="question-card"
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
      >
        <h3>{patternQuestion.question}</h3>

        <div className="sequence">
          {patternQuestion.sequence.map((item, i) => (
            <span key={i} className="seq-item">
              {item}
            </span>
          ))}
        </div>

        <div className="options">
          {patternQuestion.options.map((option, index) => (
            <motion.button
              key={index}
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.95 }}
              className={`option-btn ${
                selectedAnswer === option
                  ? feedback === "correct"
                    ? "correct"
                    : "wrong"
                  : ""
              }`}
              onClick={() => handleAnswer(option)}
              disabled={completed}
            >
              {option}
            </motion.button>
          ))}
        </div>

        {/* Feedback Display */}
        {feedback === "correct" && (
          <motion.div
            className="feedback correct"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
          >
            <CheckCircle size={40} color="#16a34a" />
            <p>Nice work! You spotted the pattern correctly!</p>
          </motion.div>
        )}

        {feedback === "wrong" && (
          <motion.div
            className="feedback wrong"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
          >
            <XCircle size={40} color="#ef4444" />
            <p>Not quite, try again!</p>
          </motion.div>
        )}

        {/* Completion Message */}
        {completed && (
          <motion.div
            className="completed"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
          >
            <Star size={50} color="#facc15" />
            <p>Warm-up Complete!</p>
          </motion.div>
        )}
      </motion.div>

      {/* üß† Subject-Specific Section */}
      {completed && (
        <motion.div
          className="phase-container"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.3 }}
        >
          <h2 className="subject-heading">Now continue with {subject}</h2>
          {renderActivity()}
        </motion.div>
      )}
    </div>
  );
};

export default IntermediateActivities;
