import React, { useEffect, useState } from "react";
import Confetti from "react-confetti";
import {
  Smile,
  Frown,
  Star,
  Square,
  Circle,
  Triangle,
} from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import "./FoundationActivities.css";
import FoundationMathActivity from "./subjects/FoundationMathActivity";
import FoundationEnglishActivity from "./subjects/FoundationEnglishActivity";
import FoundationArtActivity from "./subjects/FoundationArtActivity";

const FoundationActivities = ({ subject, grade, onBack }) => {
  const [showConfetti, setShowConfetti] = useState(false);
  const [feedback, setFeedback] = useState("neutral");
  const [completed, setCompleted] = useState(false);
  const [music, setMusic] = useState(null);

  const [shapes, setShapes] = useState([
    { id: "circle", label: "Circle", color: "#60a5fa", icon: <Circle /> },
    { id: "square", label: "Square", color: "#f87171", icon: <Square /> },
    { id: "triangle", label: "Triangle", color: "#fbbf24", icon: <Triangle /> },
  ]);

  const [slots] = useState(["circle", "square", "triangle"]);

  // 🎵 Background music
  useEffect(() => {
    const audio = new Audio("/sounds/happy-theme.mp3");
    audio.loop = true;
    audio.volume = 0.25;
    audio.play();
    setMusic(audio);
    return () => audio.pause();
  }, []);

   const renderActivity = () => {
    switch (subject.toLowerCase()) {
      case "mathematics":
        return <FoundationMathActivity grade={grade} />;
      case "english":
        return <FoundationEnglishActivity grade={grade} />;
      case "art":
        return <FoundationArtActivity grade={grade} />;
      default:
        return <p>No activity found for this subject.</p>;
    }
 };

  // 🔊 Voice feedback
  const speak = (text) => {
    if ("speechSynthesis" in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.lang = "en-US";
      utterance.pitch = 1.1;
      utterance.rate = 0.9;
      utterance.volume = 1;
      window.speechSynthesis.cancel();
      window.speechSynthesis.speak(utterance);
    }
  };

  const handleDragEnd = (result) => {
    if (!result.destination) return;
    const { destination } = result;

    if (destination.droppableId === result.draggableId) {
      setFeedback("correct");
      new Audio("/sounds/success.mp3").play();
      speak("Great job!");
      setShowConfetti(true);
      setShapes((prev) => prev.filter((s) => s.id !== result.draggableId));

      if (shapes.length === 1) {
        setTimeout(() => setCompleted(true), 400);
        speak("You matched all the shapes! Well done!");
      }

      setTimeout(() => setShowConfetti(false), 2000);
    } else {
      setFeedback("wrong");
      new Audio("/sounds/error.mp3").play();
      speak("Oops! Try again!");
    }
  };

  return (
    <div className="foundation-activities">
      {showConfetti && <Confetti />}
      <button className="back-btn" onClick={onBack}>← Back</button>

      <h2>{subject} - Foundation Activity</h2>
      <p className="grade-info">Grade {grade} | Foundation Phase</p>

      <AnimatedMascot feedback={feedback} />

      <h3 className="activity-title">Shape Matching Game</h3>
      <p className="instructions">Drag the shapes into the correct outlines!</p>

      <DragDropContext onDragEnd={handleDragEnd}>
        <div className="shape-board">
          {/* Draggable shapes */}
          <Droppable droppableId="shapes" direction="horizontal">
            {(provided) => (
              <div className="shape-row" ref={provided.innerRef} {...provided.droppableProps}>
                {shapes.map((shape, index) => (
                  <Draggable key={shape.id} draggableId={shape.id} index={index}>
                    {(provided) => (
                      <motion.div
                        className="shape-item"
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        style={{
                          backgroundColor: shape.color,
                          ...provided.draggableProps.style,
                        }}
                        whileTap={{ scale: 0.9 }}
                      >
                        {shape.icon}
                      </motion.div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>

          {/* Drop slots */}
          <div className="slots-row">
            {slots.map((slot) => (
              <Droppable droppableId={slot} key={slot}>
                {(provided) => (
                  <div className="shape-slot" ref={provided.innerRef} {...provided.droppableProps}>
                    {slot === "circle" && <Circle color="#60a5fa" size={60} />}
                    {slot === "square" && <Square color="#f87171" size={60} />}
                    {slot === "triangle" && <Triangle color="#fbbf24" size={60} />}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            ))}
          </div>
        </div>
      </DragDropContext>
       <div className="phase-container">
      <button className="back-btn" onClick={onBack}>
        ← Back
      </button>
      <h2>Foundation Phase: {subject}</h2>
      {renderActivity()}
    </div>
    

      {completed && (
        <motion.div className="congrats" initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
          <Star size={60} color="#facc15" />
          <p>Excellent work! 🎉</p>
        </motion.div>
      )}
    </div>
  );
};

// 🧸 Mascot animations
const AnimatedMascot = ({ feedback }) => {
  const variants = {
    neutral: { scale: 1 },
    correct: { scale: [1, 1.2, 1], rotate: [0, 10, -10, 0], transition: { duration: 0.8 } },
    wrong: { scale: [1, 0.9, 1], rotate: [0, -10, 10, 0], transition: { duration: 0.6 } },
  };

  return (
    <motion.div className="mascot" variants={variants} animate={feedback}>
      <AnimatePresence mode="wait">
        {feedback === "correct" && <Smile size={70} color="#10b981" key="happy" />}
        {feedback === "wrong" && <Frown size={70} color="#ef4444" key="sad" />}
        {feedback === "neutral" && <Smile size={70} color="#3b82f6" key="neutral" />}
      </AnimatePresence>
    </motion.div>
  );
};

export default FoundationActivities;
