import React, { useState, useEffect } from "react";
import { BookOpen, CheckCircle2, XCircle } from "lucide-react";
import "./IntermediateEnglishActivity.css";

const words = [
  { question: "Select the correct spelling:", options: ["beutiful", "beautiful", "beatiful"], answer: "beautiful" },
  { question: "Which word is a noun?", options: ["run", "dog", "quickly"], answer: "dog" },
  { question: "Choose the synonym of 'happy':", options: ["sad", "joyful", "angry"], answer: "joyful" },
  { question: "Which word is an adjective?", options: ["bright", "run", "slowly"], answer: "bright" },
  { question: "Choose the correct article: ___ apple", options: ["a", "an", "the"], answer: "an" },
];

const EnglishActivity = () => {
  const [index, setIndex] = useState(0);
  const [selected, setSelected] = useState("");
  const [feedback, setFeedback] = useState("");
  const [score, setScore] = useState(0);

  const handleSelect = (option) => {
    setSelected(option);
    if (option === words[index].answer) {
      setFeedback("correct");
      setScore(score + 1);
      playSound("success");
      setTimeout(() => nextQuestion(), 1000);
    } else {
      setFeedback("wrong");
      playSound("error");
    }
  };

  const nextQuestion = () => {
    setFeedback("");
    setSelected("");
    if (index < words.length - 1) setIndex(index + 1);
    else setIndex(0);
  };

  const playSound = (type) => {
    const audio = new Audio(
      type === "success"
        ? "https://cdn.pixabay.com/download/audio/2022/03/15/audio_1a3c7b9dc3.mp3?filename=success-1-6297.mp3"
        : "https://cdn.pixabay.com/download/audio/2022/03/15/audio_00f9c8d21f.mp3?filename=error-1-126517.mp3"
    );
    audio.play();
  };

  return (
    <div className="english-activity">
      <h2><BookOpen className="icon" /> English Challenge</h2>

      <p className="question">{words[index].question}</p>
      <div className="options">
        {words[index].options.map((option) => (
          <button
            key={option}
            className={`option-btn ${selected === option ? "selected" : ""}`}
            onClick={() => handleSelect(option)}
          >
            {option}
          </button>
        ))}
      </div>

      <div className="feedback">
        {feedback === "correct" && <div className="correct"><CheckCircle2 /> Correct!</div>}
        {feedback === "wrong" && <div className="wrong"><XCircle /> Try again!</div>}
      </div>

      <div className="scoreboard">Score: {score}</div>
    </div>
  );
};

export default EnglishActivity;
